var enums = enums || {
    keyboard: {}
};
function keyEnter(){
    return {
        restrict: "A",
        link: function(scope, elem, attrs){
            function keyEnter(){
                scope.$eval(attrs.keyEnter);
            }
            elem.on('keydown', function(e){
                if(e.keyCode == enums.keyboard.ENTER){
                    keyEnter();
                    scope.$apply();
                }
            });
        }
    };
}
function keyEsc(){
    return {
        restrict: "A",
        link: function(scope, elem, attrs){
            function keyEsc(){
                scope.$eval(attrs.keyEsc);
            }
            elem.on('keydown', function(e){
                if(e.keyCode == enums.keyboard.ESCAPE){
                    keyEsc();
                    scope.$apply();
                }
            });
        }
    };
}
