    $scope.initializeLocale = function() {
        LocaleService.getSupportedLocales({
        }, function success(data) {
            $scope.supportedLocales = data.list;
            $('.dropdown-toggle').prop('disabled', false);
        }, function error(date) {
            $('.dropdown-toggle').prop('disabled', true);
        });
    };
    $scope.setLocale = function() {
        LocaleService.updateUserPreferredLocale(
            {
                locale: $scope.selectedLocale,
                locale_token: locale_token,
            },
            (function success(data){
                location.reload();
            }),
            (function error(data) {
                // Do nothing here
            })
        );
    };
